/* Welcome Animation */

.wrapper {
  height: 5vh;
  display: flex;
  align-items: center;
  justify-content: center;
}

.welcome {
  width: 21ch;
  animation: typing 3s steps(21), blink .5s step-end infinite alternate;
  white-space: nowrap;
  overflow: hidden;
  border-right: 3px solid;
  font-family: var(--body-font);
  font-size: 1.25em;
  font-weight: var(--font-medium);
  color: var(--text-color);
}

@keyframes typing {
  from {
    width: 0
  }
}
    
@keyframes blink {
  50% {
    border-color: transparent;
  }
}

#sleep-animation {
    display: none;
}

#desk-animation {
    display: none;
}

/* Light Mode Desk Animation */ 

svg {
  width: 256px;
  height: 256px;
}

.svg__animated {
  width: 220px;
  height: 30vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: var(--body-color);
}

/* SMOKE */
#smoke-1 {
  stroke-dasharray: 0, 10;
  animation: smoke 6s ease infinite;
}

#smoke-2 {
  stroke-dasharray: 0, 10;
  animation: smoke 6s 0.5s ease infinite;
}

@keyframes smoke {
  0% { stroke-dasharray: 0, 10; }
  50% { stroke-dasharray: 10, 0; }
  100% { stroke-dasharray: 10, 0; opacity: 0; }
}

/* WRITING */
#line-1 {
  opacity: 0;
  animation: writing 0.5s linear forwards;
}

#line-2 {
  opacity: 0;
  animation: writing 0.5s 1s linear forwards;
}

#line-3 {
  opacity: 0;
  animation: writing 0.5s 1.5s linear forwards;
}

#line-4 {
  opacity: 0;
  animation: writing 0.5s 2s linear forwards;
}

@keyframes writing {
  0% { width: 0px; opacity: 1;}
  100% { width: 14px; opacity: 1;}
}

/* Dark Mode Sleep Animation */

.sleep__animation {
    margin: 0;
    background: #342b4a;
    width: 0px;
    height: 0px;
    position: absolute;
    top: 50%;
    left: 27%;
    font-family: "Poppins", sans-serif;
    margin: 0;
    background: #342b4a;
    transform: scale(0.38);
}
.sleep__animation .bed {
    background: #332944;
    width: 365px;
    height: 580px;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    border-radius: 10px;
    box-shadow: inset 0px 30px #262038, -50px 7px 0px 10px #262038;
}
.sleep__animation .bed .bed-shadow {
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    overflow: hidden;
}
.sleep__animation .bed .bed-shadow .window-light {
    background: #5a5b89;
    right: -40px;
    top: 109px;
    height: 139px;
}
.sleep__animation .bed .bed-shadow .window-light:nth-child(2) {
    top: 261px;
}
