/* Welcome Animation */

.wrapper {
  height: 5vh;
  display: flex;
  align-items: center;
  justify-content: center;
}

.welcome {
  width: 21ch;
  animation: typing 3s steps(21), blink .5s step-end infinite alternate;
  white-space: nowrap;
  overflow: hidden;
  border-right: 3px solid;
  font-family: var(--body-font);
  font-size: 1.25em;
  font-weight: var(--font-medium);
  color: var(--text-color);
}

@keyframes typing {
  from {
    width: 0
  }
}
    
@keyframes blink {
  50% {
    border-color: transparent;
  }
}

#sleep-animation {
    display: none;
}

#desk-animation {
    display: none;
}

/* Light Mode Desk Animation */ 

svg {
  width: 256px;
  height: 256px;
}

.svg__animated {
  width: 220px;
  height: 30vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: var(--body-color);
}

/* SMOKE */
#smoke-1 {
  stroke-dasharray: 0, 10;
  animation: smoke 6s ease infinite;
}

#smoke-2 {
  stroke-dasharray: 0, 10;
  animation: smoke 6s 0.5s ease infinite;
}

@keyframes smoke {
  0% { stroke-dasharray: 0, 10; }
  50% { stroke-dasharray: 10, 0; }
  100% { stroke-dasharray: 10, 0; opacity: 0; }
}

/* WRITING */
#line-1 {
  opacity: 0;
  animation: writing 0.5s linear forwards;
}

#line-2 {
  opacity: 0;
  animation: writing 0.5s 1s linear forwards;
}

#line-3 {
  opacity: 0;
  animation: writing 0.5s 1.5s linear forwards;
}

#line-4 {
  opacity: 0;
  animation: writing 0.5s 2s linear forwards;
}

@keyframes writing {
  0% { width: 0px; opacity: 1;}
  100% { width: 14px; opacity: 1;}
}

/* Dark Mode Sleep Animation */

.sleep__animation {
    margin: 0;
    background: #342b4a;
    width: 0px;
    height: 0px;
    position: absolute;
    top: 50%;
    left: 27%;
    font-family: "Poppins", sans-serif;
    margin: 0;
    background: #342b4a;
    transform: scale(0.38);
}
.sleep__animation .bed {
    background: #332944;
    width: 365px;
    height: 580px;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    border-radius: 10px;
    box-shadow: inset 0px 30px #262038, -50px 7px 0px 10px #262038;
}
.sleep__animation .bed .bed-shadow {
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    overflow: hidden;
}
.sleep__animation .bed .bed-shadow .window-light {
    background: #5a5b89;
    right: -40px;
    top: 109px;
    height: 139px;
}
.sleep__animation .bed .bed-shadow .window-light:nth-child(2) {
    top: 261px;
}
.sleep__animation .bed .bed-head {
    width: 400px;
    height: 25px;
    background: #3c3557;
    border-radius: 5px;
    position: absolute;
    top: -20px;
    left: 50%;
    transform: translateX(-50%);
}
.sleep__animation .bed .ground-cloth {
    width: 340px;
    height: 210px;
    background: #565881;
    position: absolute;
    top: 20px;
    left: 50%;
    transform: translateX(-50%);
    border-radius: 10px 10px 0 0;
    overflow: hidden;
}
.sleep__animation .bed .ground-cloth:after {
    content: " ";
    width: 100%;
    height: 15px;
    background: #4b4d73;
    position: absolute;
    bottom: 0;
    left: 0;
}
.window-light {
    position: absolute;
    width: 235px;
    height: 145px;
    right: 30px;
    bottom: -35px;
    border-radius: 5px;
    background: #ff766c;
    transform: skewY(-10deg);
}
.sleep__animation .bed .ground-cloth .window-light:first-child {
    right: -216px;
    top: 56px;
}
.sleep__animation .bed .ground-cloth .under-pillow-shadows {
    width: 100px;
    height: 65px;
    position: absolute;
    bottom: 0;
    left: 110px;
    overflow: hidden;
}
.sleep__animation .bed .ground-cloth .under-pillow-shadows .head-shadow {
    width: 92px;
    height: 90px;
    border-radius: 0 0 46px 46px;
    position: absolute;
    bottom: 31px;
    left: 2px;
    background: #4b4d76;
}
.sleep__animation .bed .ground-cloth .under-pillow-shadows .neck-shadow {
    width: 22px;
    height: 35px;
    position: absolute;
    bottom: 7px;
    left: 36px;
    background: #4b4d76;
}
.sleep__animation .bed .ground-cloth .pillow {
    width: 230px;
    height: 110px;
    position: absolute;
    border-radius: 8px;
    top: 25px;
    left: 50%;
    transform: translateX(-50%);
    background: #453e5f;
    box-shadow: 0px 10px 0px 4px #4b4d76;
    overflow: hidden;
}
.sleep__animation .bed .ground-cloth .pillow .window-light {
    right: -25px;
    top: 83px;
}
.sleep__animation .bed .ground-cloth .pillow .pillow-shadows {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border-radius: 8px;
    box-shadow: inset 0px -5px 0px #453e5f;
    overflow: hidden;
}
.sleep__animation .bed .ground-cloth .pillow .pillow-shadows .ear-shadow {
    background: #352a4a;
    border-radius: 50%;
    width: 40px;
    height: 32px;
    position: absolute;
    bottom: -8px;
    left: 46px;
}
.sleep__animation .bed .ground-cloth .pillow .pillow-shadows .head-shadow {
    background: #352a4a;
    border-radius: 50%;
    width: 71px;
    height: 90px;
    position: absolute;
    bottom: -6px;
    left: 54px;
    transform: rotate(10deg);
}
.sleep__animation .bed .ground-cloth .boy {
    position: absolute;
    left: 50%;
    bottom: 0;
    transform: translateX(-50%);
    z-index: 99;
}
.sleep__animation .bed .ground-cloth .boy .shoulder {
    width: 110px;
    height: 110px;
    background: #473e5b;
    border-radius: 40px;
    position: absolute;
    left: 50%;
    bottom: -62px;
    transform: scaleY(0.5) translateX(-50%) rotate(-45deg);
}
.sleep__animation .bed .ground-cloth .boy .neck {
    width: 22px;
    height: 35px;
    position: absolute;
    left: 50%;
    bottom: 15px;
    transform: translateX(-50%);
    background: #3e385a;
    border-radius: 50px / 20px;
    box-shadow: inset 0px -20px 0px #b34c39;
}
.sleep__animation .bed .ground-cloth .boy .ears {
    position: absolute;
    left: 50%;
    bottom: 107px;
    transform: translateX(-50%);
}
.sleep__animation .bed .ground-cloth .boy .ears:after, .sleep__animation .bed .ground-cloth .boy .ears:before {
    content: " ";
    position: absolute;
    left: -60px;
    width: 18px;
    height: 18px;
    background: #373461;
    border: 5px solid #b34c39;
    border-radius: 50%;
}
.sleep__animation .bed .ground-cloth .boy .ears:after {
    left: 32px;
}
.sleep__animation .bed .ground-cloth .boy .head {
    width: 92px;
    height: 90px;
    border-radius: 0 0 46px 46px;
    position: absolute;
    left: 50%;
    bottom: 43px;
    transform: translateX(-50%);
    background: #e36559;
    box-shadow: inset 0px -7px 0px #b34c39;
}
.sleep__animation .bed .ground-cloth .boy .head .nose {
    width: 13px;
    height: 22px;
    background: #b54b3e;
    border-radius: 10px;
