@keyframes yawnForMouth {
    0%, 50%, 100% {
        bottom: 22px;
        width: 24px;
        border-radius: 5px 5px 12px 12px;
    }
    75% {
        bottom: 18px;
        width: 12px;
        border-radius: 12px;
    }
}
@keyframes yawnForNose {
    0%, 50%, 100% {
        bottom: 45px;
    }
    75% {
        bottom: 42px;
    }
}
@keyframes yawnForEyes {
    0%, 50%, 100% {
        bottom: 66px;
    }
    75% {
        bottom: 65px;
    }
}

@keyframes catHeadShake {
    0%, 67%, 100% {
        transform: rotate(0deg);
    }
    70%, 97% {
        transform: rotate(-10deg);
    }
}

@keyframes phoneLCD {
    0%, 70%, 100% {
        background: black;
    }
    72%, 93% {
        background: white;
    }
}

@media (min-width: 767px) and (max-width: 967px) {
    div#screen-animation.screen__animation {
        position: relative;
        top: 0vh;
        right: 3vh;
        transform: scale(0.77);
        width: 100%;
        height: auto;
        z-index: 100;;
    }
}

@media screen and (max-width: 408px) {

.qualification__content {
    font-size: 0.8rem;
}

.qualification__title {
    font-size: 0.7rem;
}

.qualification__button {
    font-size: 0.8rem;
    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
}

.qualification__data {
    display: grid;
    grid-template-columns: 0.5fr max-content 0.5fr;
    column-gap: 2rem;
}
}
